DROP TABLE IF EXISTS Hashtags;
DROP TABLE IF EXISTS UserStats;
DROP TABLE IF EXISTS RawTweets;
DROP TABLE IF EXISTS Tweets;
DROP TABLE IF EXISTS Users;

CREATE TABLE Users (
  id varchar,
  name varchar,
  screen_name varchar,
  description varchar,
  created_at timestamp,
  PRIMARY KEY (id)
);

CREATE TABLE Tweets (
  created_at timestamp,
  id varchar,
  text varchar,
  user_id varchar,
  place varchar,
  in_reply_to_status_id varchar,
  in_reply_to_screen_name varchar,
  PRIMARY KEY (id),
  FOREIGN KEY (user_id) REFERENCES Users(id)
);

CREATE TABLE RawTweets (
  tweet_id varchar,
  rawtweet varchar,
  PRIMARY KEY (tweet_id),
  FOREIGN KEY (tweet_id) REFERENCES Tweets(id)
);

CREATE TABLE UserStats (
  tweet_id varchar,
  user_id varchar,
  statuses_count int,
  followers_count int,
  friends_count int,
  PRIMARY KEY (user_id),
  FOREIGN KEY (user_id) REFERENCES Users(id),
  FOREIGN KEY (tweet_id) REFERENCES Tweets(id)
);

CREATE TABLE Hashtags (
  tweet_id varchar,
  text varchar,
  FOREIGN KEY (tweet_id) REFERENCES Tweets(id)
);
