// Test of closest pair algorithms
// David Eppstein, UC Irvine, 19 Apr 1997
//
// Complain when something goes wrong (e.g. no more memory)

#include "Error.h"
#include <iostream.h>

extern void exit(int);

void error(const char * error_description)
{
	cout << "error: " << error_description << "\n";
	exit(1);
}
