content:
ext {
	tournamentMode = project.hasProperty('t') ? true : project.hasProperty('tournament')
}

apply plugin: 'java'
apply plugin: 'eclipse'
apply plugin: 'application'

mainClassName = tournamentMode ? "connectK.tournament.TournamentMain" : "connectK.ConnectKGUI"

sourceCompatibility = 1.8
targetCompatibility = 1.8

repositories {
	jcenter()
}

dependencies {
	compile group:'org.slf4j', name: 'slf4j-api', version: '1.7.21'
	compile group: 'ch.qos.logback', name: 'logback-core', version: '1.1.7'
	compile group: 'ch.qos.logback', name: 'logback-classic', version: '1.1.7'
	// compile fileTree(dir: 'Libs', include: ['*.jar'])
}

jar {
	from('src/main/java') {
	 include 'icon.png'
	}
  manifest {
    attributes "Main-Class": "$mainClassName"
  }

  from {
    configurations.compile.collect { it.isDirectory() ? it : zipTree(it) }
  }

  archiveName(tournamentMode ? "Tournament.jar" :  "ConnectK.jar")
}
