}

{
[statement]
LET [variableName] [number] ;
PRINT [variableName] ;
GOTO [number] ;
END ;
ADD [variableName] [number] ;
SUB [variableName] [number] ;
MUL [variableName] [number] ;
DIV [variableName] [number] ;
IF [variableName] [comparisonOp] [number] THEN [number] ;
GOSUB [number] ;
RETURN ;
}

